import React from 'react';
import { Line } from 'react-chartjs-2';
import { format } from 'date-fns';

function LineGraph({ data }) {
  console.log('CCCCCCCCCCCC ', data);
  // Check if data is an array
  if (!Array.isArray(data)) {
    return <div>Error: Data is not an array</div>;
  }
  const convertDateFormat = (inputDate) => {
    // Split the input date string into components
    const [datePart, timePart] = inputDate.split(' ');
  
    // Split the date part into day, month, and year
    const [day, month, year] = datePart.split('-');
  
    // Reconstruct the date string in the desired format
    const outputDate = `${year}-${month}-${day}T${timePart}`;
  
    return outputDate;
  }
  // Extract Time, Actual Power, and Predicted Power from the data array
  const parsedData = data.map(item => ({
    ...item,
    Time: convertDateFormat(item.Time)
  }));
  console.log('PPPPPPPPPPPPPPPPP' , parsedData);
  const labels = parsedData.map(item => convertDateFormat(item.Time));
  console.log('KKKKKK' , labels);
  const actualPower = parsedData.map(item => item['Actual Power']);
  const predictedPower = parsedData.map(item => item['Predicted Power']);

  // Define the chart data
  const chartData = {
    labels: labels,
    datasets: [
      {
        label: 'Actual Power',
        data: actualPower,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      },
      {
        label: 'Predicted Power',
        data: predictedPower,
        borderColor: 'rgb(255, 99, 132)',
        tension: 0.1
      }
    ]
  };

  // Define the chart options
  const chartOptions = {
    scales: {
      x: {
        type: 'time',
        title: {
          display: true,
          text: 'Time'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Power'
        }
      }
    }
  };

  return (
    <div>
      <h2>Line Graph</h2>
      <Line data={chartData} options={chartOptions} />
    </div>
  );
}

export default LineGraph;
